            if (strCompType.ToUpper() == "NC")  // No Current Complaint
            {
                sql = string.Empty;
                sql = " SELECT TO_CHAR(OCCURANCE_DT,'DD-Mon-YY HH24:MI AM') REG_DATE ,REG_MODULE_NAME Source,  ";
                sql = sql + "  (SELECT FAULT FROM ONM_DVB_FAULT WHERE  FCODE = OFC.FCODE)  ";
