                    sql = sql + "  AND OFC.CLOSING_DT >=TO_DATE('" + strStartDate + " 00:00:00','dd/Mon/yyyy HH24:MI:SS') AND OFC.CLOSING_DT <=  TO_DATE('" + strEndDate + " 23:59:59','dd/Mon/yyyy HH24:MI:SS') ";
