        {
            try
            {
                string strSrNo = SelectNextVal();

                //New Added
                string _ncrlst = "";
                string[] _arrnctyp = _sNCType.Trim().Split('|');

                for (int i = 0; i < _arrnctyp.Length; i++)
                {
                    if (!string.IsNullOrEmpty(_arrnctyp[i].ToString()))
