            if (UserID != "")
                sql += " AND T.USER_ID='" + UserID + "'";

            sql += " AND TRUNC(M.MACHINE_ENTRY_DT) BETWEEN TRUNC(TO_DATE('" + Fromdate + "','dd/mm/yyyy')) AND TRUNC(TO_DATE('" + Todate + "','dd/mm/yyyy'))";

            sql += " UNION ALL ";

