                    sql += "  WHERE  A.SNO_ID = B.OBSER_ID  ";
                    if (strSubDiv != "ALL")
                    {
                        sql = sql + " AND UPPER(CIRCLE) =UPPER('" + strSubDiv + "') ";
                    }
                    sql += " AND TRUNC(A.ENTRY_DATE) BETWEEN TRUNC(TO_DATE('" + strDate + "','dd/mm/yyyy')) AND TRUNC(TO_DATE('" + strToDate + "','dd/mm/yyyy')) ";
